  function CalcTListPos(t)
    local oldp = tlist_offset
    if t > oldp-2 and t < oldp+T_butt_cnt-2 then
      return oldp
    else
      return math.max(math.min(t-(math.floor(T_butt_cnt/2)-1), (#tracks+1)-(T_butt_cnt-2)),0)  
    end
  end
  
