          if dval then
            local nonly
            if gtab.numonly then
              nonly = ''
            end        
            gtab.vals[i].dover = roundX(dval, gtab.val_dp, nonly, gtab.abbrev)
          end
        end
        
        --Gauge_SortVals()
      end
    end
    
  end
  
