          if gfx and #gfx > 0 then
          
            for g = 1, #gfx do
        
              if gfx[g].stretchmode == nil then gfx[g].stretchmode = 1 end
              if gfx[g].edgesz == nil then gfx[g].edgesz = 8 end              
        
            end
          end
        end
      end
    else
