  function table.copy(t)
    if t == nil then return nil end
    local u = { }
    for k, v in pairs(t) do u[k] = v end
    return setmetatable(u, getmetatable(t))
  end
  
  function table.deepcopy(o, seen)
    seen = seen or {}
    if o == nil then return nil end
    if seen[o] then return seen[o] end
  
  
    local no = {}
    seen[o] = no
    setmetatable(no, deepcopy(getmetatable(o), seen))
  
    for k, v in next, o, nil do
      k = (type(k) == 'table') and k:deepcopy(seen) or k
      v = (type(v) == 'table') and v:deepcopy(seen) or v
      no[k] = v
    end
    return no
  end
  
  function table_slowsort_val(tbl)
   
     local dtbl = {}
     local rtbl
     local cnt = #tbl
     if cnt > 0 then  
       for st = 1, cnt do
         if st == 1 then
           --insert 
           table.insert(dtbl, tbl[st])
         else
           local inserted = false
           local dcnt = #dtbl
           for dt = 1, dcnt do
             if dtbl[dt].val then
               if tbl[st] and dtbl[dt] and nz(tonumber(tbl[st].val),0) > nz(tonumber(dtbl[dt].val),0) then
                 table.insert(dtbl, dt, tbl[st])
                 inserted = true
                 break
               end
             else
               break
             end
           end 
           if inserted == false then
             table.insert(dtbl, tbl[st])
           end
         end
       end
       rtbl = {}    
       for dt = #dtbl, 1, -1 do
         rtbl[#dtbl-(dt-1)] = dtbl[dt]
       end
     end
     return rtbl
  end
  
  function cycledata_slowsort(tbl)
  
    local dtbl = {}
    if tbl.statecnt > 0 then  
      for st = 1, tbl.statecnt do
        if st == 1 then
          --insert 
          table.insert(dtbl, tbl[st])
        else
          local inserted = false
          local dcnt = #dtbl
          for dt = 1, dcnt do
            if dtbl[dt].val then
              if tbl[st] and dtbl[dt] and nz(tonumber(tbl[st].val),0) > nz(tonumber(dtbl[dt].val),0) then
                table.insert(dtbl, dt, tbl[st])
                inserted = true
                break
              end
            else
              break
            end
          end 
          if inserted == false then
            table.insert(dtbl, tbl[st])
          end
        end
      end    
    end
    
    local otbl = {statecnt = tbl.statecnt,
                  selected = tbl.selected,
                  mapptof = tbl.mapptof,
                  draggable = tbl.draggable,
                  spread = tbl.spread,
                  pos = 1,
                  {}}
    local dcnt = #dtbl
    for i = 1, dcnt do
      otbl[i] = {val = dtbl[dcnt-(i-1)].val, dispval = dtbl[dcnt-(i-1)].dispval, dv = dtbl[dcnt-(i-1)].dv}
    end
                  
    return otbl    
    
  end
      
