        for s = 1, #strips do
          for p = 1,4 do
            for c = 1, #strips[s][p].controls do
            
              local ctl = strips[s][p].controls[c]
              Envelope_SetProps(s,p,c,active,nz(visible,true),armed, clear)
            
            end
          end  
        end
      else
        local s, p = strip, page
        if strip and page and strips[s] then
          reaper.PreventUIRefresh(1)          
          for c = 1, #strips[s][p].controls do
          
            local ctl = strips[s][p].controls[c]
