  function LoadGraphics(fn)
  
    local iidx
    local gfx_sel = -1
    for k = 0, #graphics_files do
      if graphics_files[k] and graphics_files[k].fn == fn then
        gfx_sel = k
        break
      end
    end
    if gfx_sel ~= -1 then
      
      if graphics_files[gfx_sel].imageidx == nil then
