  function Table_RemoveNils(srctbl, dcnt)
  
    local tbl = {}
    
    if dcnt > 0 then
      for i = 1, dcnt do
        if srctbl[i] ~= nil then
          table.insert(tbl, srctbl[i])
        end
      end
    end
    return tbl
      
  end

  ------------------------------------------------------------

  function Table_RemoveEntry(srctbl, dcnt, dremidx)
  
    local tbl = {}
    
    if dcnt > 0 then
      for i = 1, dcnt do
        if i ~= dremidx then
          table.insert(tbl, srctbl[i])
        end
      end
    end
    return tbl
      
  end
  
