                          end
                        end
                      end
                    end
                  end
                end
              end
            end
          end
        end
      end
    end

    local swstart = #switchers+1
    local nsflag = false
    
    for s = 1, #loaddata.stripdata do
    
      if loaddata.stripdata and loaddata.stripdata[s] then
    
        for p = 1, 4 do
    
          if loaddata.stripdata[s][p] then
