  function frameScale(m, v)
    
    if m == 1 then
      return v
    elseif m == 2 then
      return outCirc(v)
    else
      return v
    end
  
  end
  
  function ctlScale(m, v)
  
    local mm = scalemode_table[m]
    return v^mm
  
  end

  function ctlScaleInv(m, v)
  
    local mm = 1/scalemode_table[m]
    return v^mm
  
  end
  
  function macScale(m, v)
    if m == 1 then
      return v
    elseif m == 2 then
      return inSine(v)
    elseif m == 3 then
      return outSine(v)    
    elseif m == 4 then
      return inOutSine(v)        
    elseif m == 5 then
      return inCubic(v)
    elseif m == 6 then
      return outCubic(v)
    elseif m == 7 then
      return inOutCubic(v)
    elseif m == 8 then
      return inQuart(v)
    elseif m == 9 then
      return outQuart(v)
    elseif m == 10 then
      return inOutQuart(v)
    else
      return v
    end
  end
  
  --b=0 c=1 d=1
  function inSine(t)
    if t < 0 then
      return -(-1 * math.cos(-t * (pi / 2)) + 1)
    else
      return -1 * math.cos(t * (pi / 2)) + 1
    end
  end
  
  function outSine(t)
    return 1 * math.sin(t * (pi / 2))
  end
  
  function inOutSine(t)
    if t < 0 then
      return 1/2 * (math.cos(pi * t) - 1)
    else
      return -1/2 * (math.cos(pi * t) - 1)
    end
  end
  
  function inCubic(t)
    return t^3
  end
  
  function outCubic(t)
    if t < 0 then
      t = -t - 1
      return -(t^3 + 1)
    else
      t = t - 1
      return t^3 + 1
    end
  end
  
  function inOutCubic(t)
    if t < 0 then
      t = -t * 2
      if t < 1 then
        return -(1/2 * t * t * t)
      else
        t = t - 2
        return -(1/2 * (t * t * t + 2))
      end
    else
      t = t * 2
      if t < 1 then
        return 1/2 * t * t * t
      else
        t = t - 2
        return 1/2 * (t * t * t + 2)
      end
    end
  end
  
  function inQuart(t)
  
    if t < 0 then
      return -(t^4)
    else
      return t^4
    end
  end
  
  function outQuart(t)
    if t < 0 then
      t = -t - 1
      return -(-1 * (t^4 - 1))    
    else
      t = t - 1
      return -1 * (t^4 - 1)
    end
  end
  
  function inOutQuart(t)
    if t < 0 then
      t = -t * 2
      if t < 1 then
        return -(1/2 * t^4)
      else
        t = t - 2
        return -(-1/2 * (t^4 - 2))
      end
    else
      t = t * 2
      if t < 1 then
        return 1/2 * t^4
      else
        t = t - 2
        return -1/2 * (t^4 - 2)
      end
    end
  end
  
  
  function inQuint(t)
    return t^5
  end

  function inQuintInv(v)
    return v^0.2
  end
  
